namespace Tecnocreaciones\Vzla\EntityBundle\Repository;

/**
 * MunicipalityRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class MunicipalityRepository extends EntityRepository
{
    function findMunicipalitiesByState($state)
    {
        return $this->findQueryMunicipalitiesByState($state)->getQuery()->getResult();
    }
    
    function findQueryMunicipalitiesByState($state)
    {
        $qb = $this->getQueryAllActive();
        $qb
                ->innerJoin('m.state', 's')
                ->andWhere('m.active = 1')
                ->andWhere('s.id = :state')
                ->setParameter('state', $state)
                ;
            $this->addSort($qb);
        return $qb;
    }
    
    protected function getAlias() {
        return "m";
    }
}
